import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({

    container: {
      backgroundColor: '#1065e9',
      padding: 10,
      borderTopLeftRadius: 10,
      borderTopRightRadius: 10,
    },
    title: {
      color: 'white',
      fontSize: 20,
      fontWeight: 'bold',
      marginBottom: 10,
    },
    text: {
      color: '#bed9ff',
      fontSize: 15,
      marginBottom: 10,
    },
    learnMore: {
      color: 'white',
      fontSize: 15,
      fontWeight: 'bold',
    },
});

export default styles;
